function (doc) {
    if (doc._id.indexOf('package!') === 0) {
        if (doc.error) {
            if (!doc.error.unrecoverable) {
                emit(['failed', Date.parse(doc.error.caughtAt), doc._id.split('!')[1]]);
            }
        } else {
            emit(['normal', Date.parse(doc.finishedAt), doc._id.split('!')[1]]);
        }
    }
}
